chrome.runtime.onInstalled.addListener(()=>{console.log("Fish Pish installed")});chrome.runtime.onMessage.addListener((t,s,n)=>{if(t.type==="ANALYZE_URL"){const r=t.fast?"/analyze/fast":"/analyze";return fetch(`http://localhost:8000${r}`,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({url:t.url,linkText:t.linkText})}).then(e=>e.json()).then(e=>n({success:!0,data:e})).catch(e=>n({success:!1,error:e.message})),!0}});
